CREATE TABLE t_user (user_id INT generated by default as identity (start with 100),user_name VARCHAR(30),credits INT,password  VARCHAR(32),last_visit timestamp,last_ip  VARCHAR(23), primary key (user_id));
CREATE TABLE t_login_log (login_log_id  INT generated by default as identity (start with 1), user_id   INT,ip  VARCHAR(23),login_datetime timestamp,primary key (login_log_id));